package leetCode.juneLeetCodingChallenge.week3;

public class LongestDuplicateSubstring {
    String S;

    class TrieNode {
        private TrieNode[] next;
        private int i;
        private int depth;

        public TrieNode(int i, int depth) {
            this.i = i;
            this.depth = depth;
        }
    }

    /**
     * Concept: while iterating through characters of the string, keep a note of the index it was seen at. This way, when we
     * see the same character again, we can start building the possible duplicate string.
     *
     * For example: say we saw 'a' at index 0, then we saw 'a' at index 5. Since we know the last position it was seen at, we
     * can now start building using two pointers, one at index 0, one at index 5, and build onwards until a mismatch occurs.
     *
     * NOTE: The best solution would be to use Rabin-Karp Rolling Hash in combination with binary search.
     *
     * The binary search would be for the length of the longest duplicate substring, we attempt to find substring with length
     * equal to the mid value.
     *
     * The rolling hash will be affected by the mid value, as the mid value represents the size of the sliding window.
     * @param S
     * @return
     */
    public String longestDupSubstring(String S) {
        if (S.length() < 1) return "";

        this.S = S;
        int maxLo = 0, maxLength = 0;

        TrieNode root = new TrieNode(0, 0);

        // Optimize by minimizing halt condition to 'i + maxLength'
        // Start at '1' due to first character will be considered upon first entry of 'addNew()'
        for (int i = 1; i + maxLength < S.length(); i++) {
            int len = addNew(root, i);
            if (len > maxLength) {
                maxLength = len;
                maxLo = i;
            }
        }
        return S.substring(maxLo, maxLo + maxLength);
    }

    private int getIndex(int raw) {
        return S.charAt(raw) - 'a';
    }

    private int addNew(TrieNode node, int i) {
        if (i + node.depth >= S.length()) return node.depth;

        // Build onwards for the previous seen location
        if (node.next == null) {
            node.next = new TrieNode[26];
            node.next[getIndex(node.i + node.depth)] = new TrieNode(node.i, node.depth + 1);
        }

        // Build onwards for the current seen location (not actually going to build if it the character is a match)
        int c = getIndex(i + node.depth);
        TrieNode child = node.next[c];
        if (child == null) {
            node.next[c] = new TrieNode(i, node.depth + 1);
            return node.depth;
        }

        // Iterate to the next round of building
        return addNew(node.next[c], i);
    }

    public static void main(String[] args) {
        String[] tests = {
                "anana",
                "abcd",
                "",
                "aaaa",
                "abacccababaccc",
                "",
                "anban"
        };

        for (int i = 0; i < tests.length; i++) {
            LongestDuplicateSubstring s = new LongestDuplicateSubstring();
            System.out.println(
                    s.longestDupSubstring(tests[i])
            );
        }
    }
}
